Bootsy.Area.prototype.showUploadLoadingAnimation = function() {
  this.find('.bootsy-upload-loader').fadeIn(200);
};

Bootsy.Area.prototype.hideUploadLoadingAnimation = function() {
  this.find('.bootsy-upload-loader').fadeOut(200);
};

