Bootsy.Area.prototype.showEmptyAlert = function() {
  this.find('.bootsy-empty-alert').fadeIn(200);
};

Bootsy.Area.prototype.hideEmptyAlert = function() {
  this.find('.bootsy-empty-alert').fadeOut(200);
};

